import React, {PureComponent} from 'react';
import CITIES from './cities.json';

const defaultContainer = ({children}) => <div className="control-panel">{children}</div>;

export default class ControlPanel extends PureComponent {

  _renderButton = (city, index) => {
    return (
      <div key={`btn-${index}`} className="input">
        <input
          type="radio"
          name="city"
          id={`city-${index}`}
          defaultChecked={city.city === 'Mumbai'}
          onClick={() => this.props.onViewportChange(city)}
        />
        <label htmlFor={`city-${index}`}>{city.city}</label>
      </div>
    );
  };

  render() {
    const Container = this.props.containerComponent || defaultContainer;

    return (
      <Container>
        <h3 className="hero"> Vehicle Visualization</h3>
        <div>
        <input className="form" type="text" placeholder="Enter Bus Number" name="type"></input>
        <input className="submit" type="submit" value="Submit" ></input>
        </div>


      </Container>
    );
  }
}
